import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Header from "./components/Header"; // No need for .js in imports
import LandingPage from "./pages/LandingPage";
import Signup from "./pages/Signup";
import "./styles/App.css";

function App() {
  return (
    <Router>
      <div className="App">
        {/* Permanent Header Component */}
        <Header />
        {/* Routes */}
        <Routes>
          {/* Landing Page Route */}
          <Route path="/" element={<LandingPage />} />
          {/* Signup Page Route */}
          <Route path="/signup" element={<Signup />} />
          {/* About Page Placeholder */}
          <Route
            path="/about"
            element={
              <div style={{ textAlign: "center", padding: "2rem", color: "#fff" }}>
                <h1>About Adam's DnD Hosting</h1>
                <p>
                  Welcome to Adam's DnD Hosting! This platform allows you to host and
                  participate in exciting DnD campaigns with friends and players from
                  around the world.
                </p>
              </div>
            }
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
