import React, { useState, useEffect } from "react";
import "../styles/CharacterManagement.css";
import FighterImage from "../components/images/fighter.png";
import HumanImage from "../components/images/human.png";

const Characters = () => {
  const username = localStorage.getItem("username");
  const [characters, setCharacters] = useState([]);

  useEffect(() => {
    const storedCharacters = JSON.parse(localStorage.getItem(username)) || [];
    setCharacters(storedCharacters);
  }, [username]);

  const handleCreateCharacter = () => {
    const newCharacter = {
      id: Date.now(),
      name: "New Character",
      level: 1,
      class: "Fighter",
      race: "Human",
      image: FighterImage,
      raceImage: HumanImage,
    };

    const updatedCharacters = [...characters, newCharacter];
    setCharacters(updatedCharacters);
    localStorage.setItem(username, JSON.stringify(updatedCharacters));
  };

  return (
    <div className="character-management">
      <h1>Your Characters</h1>
      <div className="character-grid">
        {characters.map((character) => (
          <div className="character-box" key={character.id}>
            <h2>{character.name}</h2>
            <p>Level: {character.level}</p>
            <div>
              <img src={character.image} alt={character.class} />
              <img src={character.raceImage} alt={character.race} />
            </div>
            <p>Class: {character.class}</p>
            <p>Race: {character.race}</p>
            <button className="details-button">More Details</button>
          </div>
        ))}
        <div className="create-character-box" onClick={handleCreateCharacter}>
          <span className="plus-icon">+</span>
          <h2>Create Character</h2>
        </div>
      </div>
    </div>
  );
};

export default Characters;
