import {
  getUser,
  addUser,
  saveCharactersForUser,
  getCharactersForUser,
} from "./database.js";

export const isAuthenticated = () => {
  return localStorage.getItem("isLoggedIn") === "true";
};

export const getLoggedInUser = () => {
  return JSON.parse(localStorage.getItem("currentUser"));
};

export const login = (username, password) => {
  const user = getUser(username);

  if (user && user.password === password) {
    localStorage.setItem("isLoggedIn", "true");
    localStorage.setItem("currentUser", JSON.stringify({ username }));
    return true;
  }

  return false;
};

export const signup = (username, password) => {
  if (getUser(username)) {
    return false;
  }

  addUser({ username, password, characters: [] });
  return true;
};

export const logout = () => {
  localStorage.removeItem("isLoggedIn");
  localStorage.removeItem("currentUser");
};

export const saveCharacters = (username, characters) => {
  saveCharactersForUser(username, characters);
};

export const getCharacters = (username) => {
  return getCharactersForUser(username);
};
