import React, { useState, useEffect } from "react";
import CharacterCreation from "../components/CharacterCreation";
import "../styles/CharacterManagement.css";
import { getLoggedInUser, saveCharacters, getCharacters } from "../utils/auths";

const Characters = () => {
  const currentUser = getLoggedInUser()?.username;
  const [characters, setCharacters] = useState([]);
  const [isCreating, setIsCreating] = useState(false);

  useEffect(() => {
    if (currentUser) {
      const userCharacters = getCharacters(currentUser);
      setCharacters(userCharacters);
    }
  }, [currentUser]);

  const handleCreateCharacter = () => {
    setIsCreating(true);
  };

  const handleCloseCreation = () => {
    setIsCreating(false);
  };

  const handleCharacterCreate = (newCharacter) => {
    const updatedCharacters = [...characters, newCharacter];
    setCharacters(updatedCharacters);
    saveCharacters(currentUser, updatedCharacters);
    setIsCreating(false);
  };

  return (
    <div className="character-management">
      <h1>Your Characters</h1>
      {characters.length === 0 && (
        <p>No characters yet. Click "Create Character" to get started!</p>
      )}
      <div className="character-grid">
        {characters.map((character, index) => (
          <div className="character-box" key={index}>
            <h2>{character.name}</h2>
            <p>Level: {character.level}</p>
            <div>
              <img src={character.image} alt={character.class} />
              <img src={character.raceImage} alt={character.race} />
            </div>
            <p>Class: {character.class}</p>
            <p>Race: {character.race}</p>
            <button className="details-button">More Details</button>
          </div>
        ))}
        <div className="create-character-box" onClick={handleCreateCharacter}>
          <span className="plus-icon">+</span>
          <h2>Create Character</h2>
        </div>
      </div>
      {isCreating && (
        <CharacterCreation
          onClose={handleCloseCreation}
          onCreate={handleCharacterCreate}
        />
      )}
    </div>
  );
};

export default Characters;
