import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Signup.css";
import { getUser, addUser, login } from "../utils/auths.js";

const Signup = () => {
  const [isLogin, setIsLogin] = useState(false);
  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const adminAccount = { username: "admin", password: "adam123", characters: [] };
    const storedUsers = JSON.parse(localStorage.getItem("users")) || [];
    const isAdminAdded = storedUsers.some(
      (user) => user.username === adminAccount.username
    );

    if (!isAdminAdded) {
      localStorage.setItem(
        "users",
        JSON.stringify([...storedUsers, adminAccount])
      );
    }
  }, []);

  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setFormData({ ...formData, [id]: value });
  };

  const handleSwitchMode = () => {
    setIsLogin(!isLogin);
    setError("");
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const { username, password } = formData;

    if (isLogin) {
      // Handle Login
      if (login(username, password)) {
        navigate("/characters");
      } else {
        setError("Invalid username or password.");
      }
    } else {
      // Handle Signup
      if (getUser(username)) {
        setError("Username already exists.");
      } else {
        const newUser = {
          username,
          password,
          characters: [],
        };

        addUser(newUser);
        alert("Account created successfully!");
        setIsLogin(true);
      }
    }
  };

  return (
    <div className="signup-page">
      <div className="form-container">
        <h1>{isLogin ? "Login" : "Create an Account"}</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              id="username"
              placeholder="Enter your username"
              value={formData.username}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              placeholder="Enter your password"
              value={formData.password}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type="submit">{isLogin ? "Log In" : "Sign Up"}</button>
          {error && <p className="error">{error}</p>}
        </form>
        <p className="switch-mode">
          {isLogin ? "Don't have an account?" : "Already have an account?"}{" "}
          <span onClick={handleSwitchMode}>
            {isLogin ? "Sign Up" : "Log In"}
          </span>
        </p>
      </div>
    </div>
  );
};

export default Signup;
