import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Header.css";
import logo from "../components/images/logo.png"; // Import the logo

const Header = () => {
  const [isSidebarOpen, setSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setSidebarOpen(!isSidebarOpen);
  };

  return (
    <header>
      <div className="header-container">
        {/* Hamburger Menu */}
        <div
          className={`menu-icon ${isSidebarOpen ? "rotated" : ""}`}
          onClick={toggleSidebar}
        >
          <div className="line"></div>
          <div className="line"></div>
          <div className="line"></div>
        </div>
        {/* Logo */}
        <Link to="/" className="header-logo">
          <img src={logo} alt="Adam's DnD Hosting Logo" />
        </Link>
        {/* Navigation Links */}
        <nav>
          <Link to="/about">About</Link>
          <Link to="/signup">Login</Link>
        </nav>
      </div>

      {/* Sidebar */}
      <div className={`sidebar ${isSidebarOpen ? "open" : ""}`}>
        <div className="sidebar-content">
          <img src={logo} alt="Adam's DnD Hosting Logo" className="sidebar-logo" />
          <ul>
            <li>
              <Link to="/characters">Characters</Link>
            </li>
            <li>
              <Link to="/about">About</Link>
            </li>
          </ul>
        </div>
      </div>
    </header>
  );
};

export default Header;
