import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { isAuthenticated, getLoggedInUser, logout } from "../utils/auths.js";
import "./Header.css";
import logo from "../components/images/logo.png";

const Header = () => {
  const [isSidebarOpen, setSidebarOpen] = useState(false);
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const navigate = useNavigate();

  const currentUser = isAuthenticated() ? getLoggedInUser()?.username : null;

  const toggleSidebar = () => {
    setSidebarOpen(!isSidebarOpen);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
    if (window.confirm("Are you sure you want to log out?")) {
      logout();
      setSidebarOpen(false);
      navigate("/signup");
    }
  };

  return (
    <header>
      <div className="header-container">
        {/* Left Section with Menu and Logo */}
        <div className="header-left">
          <div
            className={`menu-icon ${isSidebarOpen ? "open" : ""}`}
            onClick={toggleSidebar}
          >
            <div className="line"></div>
            <div className="line"></div>
            <div className="line"></div>
          </div>
          {!isSidebarOpen && (
            <Link to="/" className="header-logo">
              <img src={logo} alt="Adam's DnD Hosting Logo" />
            </Link>
          )}
        </div>

        {/* Right Section with Navigation */}
        <nav className="header-nav">
          <Link to="/about">About</Link>
          {currentUser ? (
            <div className="account-dropdown">
              <span onClick={toggleDropdown}>{currentUser}</span>
              {isDropdownOpen && (
                <div className="dropdown-menu">
                  <button className="dropdown-item">Account Settings</button>
                  <button className="dropdown-item" onClick={handleLogout}>
                    Log out
                  </button>
                </div>
              )}
            </div>
          ) : (
            <Link to="/signup">Login</Link>
          )}
        </nav>
      </div>
    </header>
  );
};

export default Header;
