import React, { useState } from "react";
import "../styles/CharacterManagement.css";
import CharacterCreation from "../components/CharacterCreation.js";

const Characters = () => {
  const [characters, setCharacters] = useState([]);
  const [isCreating, setIsCreating] = useState(false);

  const addCharacter = (newCharacter) => {
    setCharacters([...characters, newCharacter]);
    setIsCreating(false);
  };

  return (
    <div className="characters-page">
      <h1>Your Characters</h1>
      <div className="characters-grid">
        {/* Show "No Characters" message if no characters exist */}
        {characters.length === 0 ? (
          <p>No characters yet. Click "Create Character" to get started!</p>
        ) : (
          characters.map((char, index) => (
            <div key={index} className="character-card">
              <div className="character-header">
                <h2>{char.name}</h2>
                <p>Level: {char.level}</p>
              </div>
              <div className="character-details">
                <img
                  src={char.image}
                  alt={`${char.name}`}
                  className="character-image"
                />
                <p>Class: {char.class}</p>
                <p>Race: {char.race}</p>
              </div>
              <button
                className="details-btn"
                onClick={() => alert(`More details about ${char.name}`)}
              >
                More Details
              </button>
            </div>
          ))
        )}

        {/* Create Character Card */}
        <div
          className="create-character-card"
          onClick={() => setIsCreating(true)}
        >
          <p>Create New Character</p>
        </div>
      </div>

      {/* Popup for Character Creation */}
      {isCreating && (
        <CharacterCreation
          onClose={() => setIsCreating(false)}
          onCreate={addCharacter}
        />
      )}
    </div>
  );
};

export default Characters;
